package daily;

public class Reverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          String s = "velocity";
          
          
    int len =s.length();
          
          
      System.out.println();    
          
        for (int a=len-1;a>=0;a--)  
        {
        	System.out.print(s.charAt(a));
        }
        	
      System.out.println();  	
        	
       System.out.println(s.charAt(4)); 	
        	
        	
	}
	
	
	
	

}
